import React from 'react'
im

function Listitem( { books, handleSave } ) {
    return (
        <>
        {books.length > 0 ? books.map(book => {
            return (
                <Container>
        <Row>
          <Col size="md-12">
            <Jumbotron>
              <h1 className="text-center">
                <strong>(React) Google Books Search</strong>
              </h1>
              <h2 className="text-center">Search for and Save Books of Interest.</h2>
            </Jumbotron>
          </Col>
          <Col size="md-12">
            <Card title="Book Search" icon="far fa-book">
              <Form
                handleInputChange={this.handleInputChange}
                handleFormSubmit={this.handleFormSubmit}
                q={this.state.q}
              />
            </Card>
          </Col>
        </Row>
        <Row>
          <Col size="md-12">
            <Card title="Results">
              {this.state.books.length ? (
                <List>
                  {this.state.books.map(book => (
                    <Book
                      key={book.id}
                      title={book.volumeInfo.title}
                      subtitle={book.volumeInfo.subtitle}
                      link={book.volumeInfo.infoLink}
                      authors={book.volumeInfo.authors.join(", ")}
                      description={book.volumeInfo.description}
                      image={book.volumeInfo.imageLinks.thumbnail}
                      Button={() => (
                        <button
                          onClick={() => this.handleBookSave(book.id)}
                          className="btn btn-primary ml-2"
                        >
                          Save
                        </button>
                      )}
                    />
                  ))}
                </List>
              ) : (
                <h2 className="text-center">{this.state.message}</h2>
              )}
            </Card>
          </Col>
        </Row>
        <Footer />
      </Container>
        //         <div className="card"> 
        //         <div className="card-body">
        //              <div className="row">
        //                  <div className="col-md-1">
        //                     <img src={book.volumeInfo ? book.volumeInfo.imageLinks.thumbnail:"https://upload.wikimedia.org/wikipedia/commons/thumb/6/6c/No_image_3x4.svg/1200px-No_image_3x4.svg.png" }/>
        //                     <p></p>
        //                  </div>
        //                  <div className="col-md-11 pl-5">
        //                      <div className="row">
        //                          <div className="col-sm-4"> 
        //                          <h3>{book.volumeInfo.title}</h3>
        //                         <h4>Written by: {book.volumeInfo.authors} </h4>
        //                          </div>
        //                          <div className="col-sm-8 text-right">
        //                              <a href={book.volumeInfo.infoLink} className="btn btn-default">
        //                                   view
        //                              </a>
        //                              <button className="ml-2" onClick={() => 
        //                             handleSave(book)}>
        //                                   save
        //                              </button>
        //                          </div>
        //                      </div>

        //                      <p>{book.volumeInfo.description}</p>
        //                  </div>
        //              </div>
        //         </div>
        //   </div>

            )
        })   : "No Books"}

        </>
    )
}

export default Listitem
